// ==UserScript==
// @name		DisableAllRepair
// @version		2025.01.30
// @author		leo7044
// @description	disable all repair-button-functions
// @include		http*://cncapp*.alliances.commandandconquer.com/*/index.aspx*
// @icon		data:image/png;base64,
// @grant		none
// ==/UserScript==

(function () {
	var DisableAllRepairMain = function ()
	{
		function DisableAllRepairCreate()
		{
			try
			{
				var PlayerId = ClientLib.Data.MainData.GetInstance().get_Player().get_Id();
				if (PlayerId > 0)
				{
                    ClientLib.Data.MainData.GetInstance().get_Cities().get_CurrentOwnCity().RepairAll = null;
                    ClientLib.Data.MainData.GetInstance().get_Cities().get_CurrentOwnCity().RepairAllOffense = null;
                    if (MaelstromTools)
                    {
                        for (var key in MaelstromTools.Cache.getInstance().Cities)
                        {
                            MaelstromTools.Cache.getInstance().Cities[key]['Object'].get_CityRepairData().RepairAll = null;
                        }
                    }
				}
				else
				{
					setTimeout(DisableAllRepairCreate, 1000);
				}
			}
			catch(e)
			{
				console.log(e);
			}
		}
		function LoadExtension()
		{
			try
			{
				if (typeof(qx)!='undefined')
				{
					if (!!qx.core.Init.getApplication().getMenuBar())
					{
						DisableAllRepairCreate();
						return;
					}
				}
			}
			catch (e)
			{
				if (console !== undefined) console.log(e);
				else if (window.opera) opera.postError(e);
				else GM_log(e);
			}
			window.setTimeout(LoadExtension, 1000);
		}
		LoadExtension();
	};
	function Inject()
	{
		if (window.location.pathname != ("/login/auth"))
		{
			var Script = document.createElement("script");
			Script.innerHTML = "(" + DisableAllRepairMain.toString() + ")();";
			Script.type = "text/javascript";
			document.getElementsByTagName("head")[0].appendChild(Script);
		}
	}
	Inject();
})();